import React, { useEffect, useContext } from "react";
import { Link, useNavigate, useLocation } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser } from "@fortawesome/free-solid-svg-icons";
import { UserContext } from "../UserContext"; // Importar UserContext

const Header = () => {
  const { usuario, setUsuario } = useContext(UserContext); // Acceder a 'usuario' y 'setUsuario'
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    const usuarioGuardado = localStorage.getItem("usuario");
    if (usuarioGuardado) {
      setUsuario(JSON.parse(usuarioGuardado));
    }
  }, [setUsuario]);

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("usuario");
    setUsuario(null); // Limpiamos el estado global del usuario
    navigate("/login");
  };

  return (
    <header className="bg-gray-800 text-white">
      <nav className="container mx-auto px-8 py-4 flex justify-between items-center">
        <h1 className="text-lg font-bold">My Ecommerce</h1>
        <div className="flex space-x-4 items-center">
          {location.pathname === "/productos" && usuario ? (
            <>
              <div className="flex items-center space-x-2">
                <FontAwesomeIcon icon={faUser} className="text-gray-300" />
                <span className="text-gray-300">{usuario.nombre}</span>
              </div>
              <button
                onClick={handleLogout}
                className="text-red-500 hover:text-red-300 ml-4"
              >
                Cerrar Sesión
              </button>
            </>
          ) : (
            <Link to="/login" className="hover:text-gray-300">
              Iniciar Sesión
            </Link>
          )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
