import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [usuario, setUsuario] = useState(""); // Estado para el usuario
  const [contrasena, setContrasena] = useState(""); // Estado para la contraseña
  const navigate = useNavigate();

  // Función para manejar el submit del formulario
  const handleLogin = (e) => {
    e.preventDefault();

    // Aquí podrías hacer una llamada a tu API para autenticar al usuario
    // Este ejemplo asume que el login es exitoso
    const user = {
      nombre: usuario,
      token: "12345", // Esto debería ser el token real que recibes de la API
    };

    // Guardamos el usuario y el token en localStorage
    localStorage.setItem("usuario", JSON.stringify(user));
    localStorage.setItem("token", user.token);

    // Aquí actualizamos el contexto para reflejar que el usuario ha iniciado sesión
    setUsuario(user); // Actualizamos el estado global del contexto con el usuario

    // Redirigimos a la página de productos
    navigate("/productos");
  };

  return (
    <div className="login-container">
      <h2>Iniciar sesión</h2>
      <form onSubmit={handleLogin}>
        <div>
          <label htmlFor="usuario">Usuario</label>
          <input
            type="text"
            id="usuario"
            value={usuario}
            onChange={(e) => setUsuario(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="contrasena">Contraseña</label>
          <input
            type="password"
            id="contrasena"
            value={contrasena}
            onChange={(e) => setContrasena(e.target.value)}
            required
          />
        </div>
        <button type="submit">Iniciar sesión</button>
      </form>
    </div>
  );
};

export default Login;
