import React from "react";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const navigate = useNavigate();

  const handleLogin = () => {
    const usuario = { nombre: "Juan Pérez" }; // Aquí iría la lógica de autenticación.
    localStorage.setItem("usuario", JSON.stringify(usuario)); // Guarda al usuario en localStorage.
    navigate("/productos");
    window.location.reload(); // Forzar una recarga para actualizar el estado del Header.
  };

  return (
    <div>
      <h1>Iniciar Sesión</h1>
      <button onClick={handleLogin}>Iniciar Sesión</button>
    </div>
  );
};

export default Login;
