// App.jsx
import React, { useContext } from "react";
import { Routes, Route, Navigate } from "react-router-dom"; // Usamos Navigate para redirigir si es necesario
import { UserContext } from "../UserContext"; // Importa UserContext
import Header from "./Header";
import Home from "./Home";
import Login from "./Login";
import Register from "./Register";
import Productos from "./Productos";

const App = () => {
  const { usuario } = useContext(UserContext); // Accedemos al estado 'usuario' desde el contexto

  return (
    <div>
      <Header className="bg-gray-800 text-white flex justify-between items-center px-6 py-4 shadow-md" />
      <Routes>
        {/* Si no hay usuario y la ruta es /productos, redirigimos al login */}
        <Route path="/" element={<Home />} />
        <Route path="/login" element={<Login />} />
        <Route path="/register" element={<Register />} />
        
        {/* Si no hay usuario, redirigimos a /login */}
        <Route
          path="/productos"
          element={usuario ? <Productos /> : <Navigate to="/login" />}
        />
      </Routes>
    </div>
  );
};

export default App;
